public class MathIsFun
{
    public static void main(String[] args)
    {
        // TODO Auto-generated method stub
        System.out.println(Math.class);
        System.out.println(Math.E);
        System.out.println(Math.PI);
        System.out.println(Math.abs(18));
        System.out.println(Math.acos(.54));
        System.out.println(Math.addExact(3,4));
        System.out.println(Math.asin(1));
        System.out.println(Math.atan(-.3));
        System.out.println(Math.atan2(6,3));
        System.out.println(Math.cbrt(9));
        System.out.println(Math.ceil(2.3));
        System.out.println(Math.copySign(-4,2));
        System.out.println(Math.cos(0.5));
        System.out.println(Math.cosh(-0.2));
        System.out.println(Math.decrementExact(9));
        System.out.println(Math.exp(1));
        System.out.println(Math.expm1(6.999));
        System.out.println(Math.floorDiv(5,2));
        System.out.println(Math.floorMod(5,2));
        System.out.println(Math.getExponent(64));
        System.out.println(Math.hypot(3,4));
        System.out.println(Math.IEEEremainder(5.4,3.1));
        System.out.println(Math.incrementExact(8));
        System.out.println(Math.log(Math.E));
        System.out.println(Math.log10(12));
        System.out.println(Math.log1p(1));
        System.out.println(Math.max(10,100));
        System.out.println(Math.min(3,5));
        System.out.println(Math.multiplyExact(2,3));
        System.out.println(Math.negateExact(43));
    }
}
